package week1.day1;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 105;
		if (number == 0) {
			System.out.println("The number is neutral");
		} else if (number % 2 != 0) {
			System.out.println(" The number is even");
		} else {
			System.out.println("The number is odd");
		}
		
	}

}
